let Option1=document.getElementById("paymth"),Option2=document.getElementById("paypal"),DateForm=document.querySelector(".right-side__pay-method-info"),Control=document.getElementById("paypall-active"),btcPay=document.getElementById("btc-active"),bitcoin=document.getElementById("bitcoin");function toggle(){Option1.checked?(DateForm.style.display="block",Control.style.display="none",btcPay.style.display="none"):Option2.checked?(DateForm.style.display="none",Control.style.display="block",btcPay.style.display="none"):bitcoin.checked&&(DateForm.style.display="none",Control.style.display="none",btcPay.style.display="block")}Option1.addEventListener("click",toggle),Option2.addEventListener("click",toggle),bitcoin.addEventListener("click",toggle);